    public Response updateGoal(
            @HeaderParam("If-Match") final String eTagHeader,
            @PathParam("goalId") final String goalId ,
            final Goal aResource
        ) throws IOException, ServletException
    {
        // Start of user code updateGoal_init
        // End of user code
        final Goal originalResource = ObjectiverManager.getGoal(httpServletRequest, goalId);

        if (originalResource != null) {
            final String originalETag = ObjectiverManager.getETagFromGoal(originalResource);

            if ((eTagHeader == null) || (originalETag.equals(eTagHeader))) {
                // Start of user code updateGoal
                // End of user code
                final Goal updatedResource = ObjectiverManager.updateGoal(httpServletRequest, aResource, goalId);
                httpServletResponse.setHeader("ETag", ObjectiverManager.getETagFromGoal(updatedResource));
                return Response.ok().header(ObjectiverConstants.HDR_OSLC_VERSION, ObjectiverConstants.OSLC_VERSION_V2).build();
            }
            else {
                throw new WebApplicationException(Status.PRECONDITION_FAILED);
            }
        }
        else {
            throw new WebApplicationException(Status.NOT_FOUND);
        }
    }

